<template><div><h1>{{content.name}} — {{content.title}}</h1><img src="/shared/assets/avatar.jpg" width="120"/><p>{{content.bio}}</p><h2>Rock Paper Scissors</h2><div><button v-for="c in choices" :key="c" @click="play(c)">{{c}}</button><div v-if="result">{{result}}</div><div>Score: {{score.player}} - {{score.ai}}</div></div></div></template><script>import content from '../shared/content.json';export default {data(){return{content,choices:['Rock','Paper','Scissors'],result:'',score:{player:0,ai:0}}},methods:{play(choice){const ai=this.choices[Math.floor(Math.random()*3)];if(choice===ai)this.result='Tie — both '+choice;else if((choice==='Rock'&&ai==='Scissors')||(choice==='Paper'&&ai==='Rock')||(choice==='Scissors'&&ai==='Paper')){this.result='You win! '+choice+' beats '+ai;this.score.player++}else{this.result='You lose — '+ai+' beats '+choice;this.score.ai++}}}}</script>